mapper.delete:
DELETE FROM room_maintenance
WHERE id IN

    #{id}

mapper.select:
SELECT rm.*,r.name as roomName FROM room_maintenance rm left join rooms r on rm.room_id = r.id


        AND rm.room_id = #{roomId}


        AND rm.status = #{status}


        AND rm.maintenance_type = #{maintenanceType}


        AND rm.start_time >= #{startTime}


        AND rm.end_time <= #{endTime}


ORDER BY rm.start_time DESC
